// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

/// @title House escrow for single-player coinflip with off-chain provably fair result
/// @notice The backend signs the round result; contract verifies signature and pays player if valid.
contract CoinflipHouse {
    address public signer; // backend signer address
    address public owner;

    mapping(bytes32 => bool) public settled; // idempotency for roundIds

    event Funded(address indexed from, uint256 amount);
    event Withdraw(address indexed to, uint256 amount);
    event Settled(bytes32 indexed roundId, address indexed player, uint256 wager, uint256 payout);

    modifier onlyOwner() { require(msg.sender == owner, "not owner"); _; }

    constructor(address _signer) {
        owner = msg.sender;
        signer = _signer;
    }

    function setSigner(address _signer) external onlyOwner { signer = _signer; }

    function fund() external payable { emit Funded(msg.sender, msg.value); }

    function withdraw(address payable to, uint256 amount) external onlyOwner {
        require(address(this).balance >= amount, "insufficient");
        (bool ok,) = to.call{value: amount}("");
        require(ok, "transfer fail");
        emit Withdraw(to, amount);
    }

    /// @dev EIP-191 prefixed message hash
    function _toEthSignedMessageHash(bytes32 hash) internal pure returns (bytes32) {
        return keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", hash));
    }

    /// @notice Settle a round signed by backend
    /// @param roundId unique id per round
    /// @param player player address
    /// @param wager wager value in wei
    /// @param payout payout in wei (0 if lose)
    /// @param deadline timestamp after which signature is invalid
    /// @param v r
    /// @param r r
    /// @param s s
    function settle(
        bytes32 roundId,
        address payable player,
        uint256 wager,
        uint256 payout,
        uint256 deadline,
        uint8 v, bytes32 r, bytes32 s
    ) external {
        require(block.timestamp <= deadline, "expired");
        require(!settled[roundId], "already");

        bytes32 message = keccak256(abi.encode(
            keccak256("Coinflip(bytes32 roundId,address player,uint256 wager,uint256 payout,uint256 deadline)"),
            roundId, player, wager, payout, deadline
        ));
        address recovered = ecrecover(_toEthSignedMessageHash(message), v, r, s);
        require(recovered == signer, "bad sig");

        settled[roundId] = true;
        if (payout > 0) {
            require(address(this).balance >= payout, "bankroll low");
            (bool ok,) = player.call{value: payout}("");
            require(ok, "payout fail");
        }
        emit Settled(roundId, player, wager, payout);
    }

    receive() external payable { emit Funded(msg.sender, msg.value); }
}

